import React, { Component, Fragment } from 'react'

class Menu extends Component{
  constructor(props) {
    super(props)
    this.state = {
      inputValue: '炒菜',
      list:[]
    }
  }

  render() {
    return (
      <Fragment>
        <div>
          <input value={this.state.inputValue} onChange={this.inputChange.bind(this)}/>
          <button>增加菜品</button>
        </div>
        <ul>
          <li>宫保鸡丁</li>
          <li>蚂蚁上树</li>
        </ul>
      </Fragment>
    )
  }

  inputChange(e) {
    this.setState({
      inputValue:e.target.value
    })
  }
}

export default Menu
